CREATE TABLE employee (id SERIAL NOT NULL, name CHARACTER VARYING(255) NOT NULL, password CHARACTER VARYING(255), descr CHARACTER VARYING(255), gross_salary_month DOUBLE PRECISION, health_insurance_share DOUBLE PRECISION, surcharge DOUBLE PRECISION, type CHARACTER VARYING(255), working_contract CHARACTER VARYING(255), contract_hours_month DOUBLE PRECISION, limit_hours_month DOUBLE PRECISION, ref_id BIGINT, valid BIGINT, PRIMARY KEY (id));